from Crypto.PublicKey import RSA 
from Crypto.Cipher import PKCS1_OAEP


pubKeyString = ''
privateKey = ''

pubKey =  RSA.importKey(open('moodtracker.pem.pub').read())
cipher_rsa = PKCS1_OAEP.new(pubKey)


encryptedMessage = cipher_rsa.encrypt('testing123'.encode("utf-8"))

print('Encrypted Message: ')
print(encryptedMessage)

privateKey = RSA.importKey(open('moodtracker.pem').read())
cipher = PKCS1_OAEP.new(privateKey)
decrypted = cipher.decrypt(encryptedMessage)

print('Message:')
print(decrypted.decode('utf-8'))